import "./Notification.css";
import React, { useEffect, useState } from "react";

const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [appointmentData, setAppointmentData] = useState(null);

  useEffect(() => {
    const storedUsername = sessionStorage.getItem("email");

    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);

      fetch(`http://localhost:3001/appointments?email=${storedUsername}`)
        .then((res) => res.json())
        .then((data) => {
          if (data.length > 0) {
            setAppointmentData(data[0]);
          }
        })
        .catch((err) => console.error("Error fetching appointment:", err));
    }
  }, []);

  return (
    <div>
      {children}

      {isLoggedIn && appointmentData && (
        <div className="appointment-card">
          <h3 className="appointment-card__title">Appointment Details</h3>
          <p>
            <strong>Doctor:</strong> {appointmentData?.doctorName}
          </p>
          <p>
            <strong>Speciality:</strong> {appointmentData?.speciality}
          </p>
          <p>
            <strong>Name:</strong> {appointmentData?.name}
          </p>
          <p>
            <strong>Phone Number:</strong> {appointmentData?.phone}
          </p>
          <p>
            <strong>Date of Appointment:</strong> {appointmentData?.date}
          </p>
          <p>
            <strong>Time Slot:</strong> {appointmentData?.time}
          </p>
        </div>
      )}
    </div>
  );
};

export default Notification;
