import "./Notification.css";
import React, { useEffect, useState } from "react";
import Navbar from "../Navbar/Navbar";

const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState(null);
  const [appointmentData, setAppointmentData] = useState(null);
  const [isVisible, setIsVisible] = useState(true); // Thêm state để kiểm soát hiển thị

  useEffect(() => {
    const storedUsername = sessionStorage.getItem("email");
    const storedDoctorData = JSON.parse(localStorage.getItem("doctorData"));
    const storedAppointmentData = JSON.parse(
      localStorage.getItem(storedDoctorData?.name)
    );

    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    if (storedDoctorData) {
      setDoctorData(storedDoctorData);
    }

    if (storedAppointmentData) {
      setAppointmentData(storedAppointmentData);
    }
  }, []);

  // Xử lý hủy cuộc hẹn
  const handleCancelAppointment = () => {
    setIsVisible(false); // Ẩn thông báo
    localStorage.removeItem(doctorData?.name); // Xóa dữ liệu cuộc hẹn
  };

  return (
    <div>
      <Navbar />
      {children}

      {isLoggedIn && appointmentData && isVisible && (
        <div className="appointment-card">
          <div className="appointment-card__content">
            <h3 className="appointment-card__title">Appointment Details</h3>
            <p className="appointment-card__message">
              <strong>Doctor:</strong> {doctorData?.name}
            </p>
            <p className="appointment-card__message">
              <strong>Time:</strong> {appointmentData?.time}
            </p>
            <button className="cancel-button" onClick={handleCancelAppointment}>
              Hủy cuộc hẹn
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Notification;
