import "./Notification.css";
import React, { useEffect, useState } from "react";
import Navbar from "../Navbar/Navbar";

const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState(null);
  const [appointmentData, setAppointmentData] = useState(null);

  useEffect(() => {
    const storedUsername = sessionStorage.getItem("email");
    const storedDoctorData = JSON.parse(localStorage.getItem("doctorData"));
    const storedAppointmentData = JSON.parse(
      localStorage.getItem(storedDoctorData?.name)
    );

    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    if (storedDoctorData) {
      setDoctorData(storedDoctorData);
    }

    if (storedAppointmentData) {
      setAppointmentData(storedAppointmentData);
    }
  }, []);

  return (
    <div>
      {children}

      {isLoggedIn && appointmentData && (
        <div className="appointment-card">
          <h3 className="appointment-card__title">Appointment Details</h3>
          <p>
            <strong>Doctor:</strong> {doctorData?.name}
          </p>
          <p>
            <strong>Speciality:</strong> {doctorData?.speciality}
          </p>
          <p>
            <strong>Name:</strong> {appointmentData?.name}
          </p>
          <p>
            <strong>Phone Number:</strong> {appointmentData?.phone}
          </p>
          <p>
            <strong>Date of Appointment:</strong> {appointmentData?.date}
          </p>
          <p>
            <strong>Time Slot:</strong> {appointmentData?.time}
          </p>
        </div>
      )}
    </div>
  );
};

export default Notification;
