import React, { useEffect, useState } from "react";
import "./ReviewForm.css";

const ReviewForm = () => {
  const [appointments, setAppointments] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [feedback, setFeedback] = useState("");

  useEffect(() => {
    fetch("http://localhost:3001/appointmentsIC")
      .then((res) => res.json())
      .then((data) =>
        setAppointments(data.filter((appt) => appt.status !== "cancelled"))
      )
      .catch((err) => console.error(err));

    fetch("http://localhost:3001/reviews")
      .then((res) => res.json())
      .then(setReviews)
      .catch((err) => console.error(err));
  }, []);

  const handleFeedbackClick = (appt) => {
    setSelectedAppointment(appt);
    setFeedback("");
  };

  const handleSubmitReview = () => {
    if (!feedback.trim()) return alert("Please enter your feedback");

    const newReview = {
      appointmentId: selectedAppointment.id,
      doctorName: selectedAppointment.doctorName,
      feedback,
    };

    fetch("http://localhost:3001/reviews", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newReview),
    })
      .then((res) => res.json())
      .then((data) => {
        setReviews([...reviews, data]);
        setSelectedAppointment(null);
        alert("Review submitted successfully!");
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="review-form-container">
      <h2>Reviews</h2>
      <table>
        <thead>
          <tr>
            <th>Serial Number</th>
            <th>Doctor Name</th>
            <th>Doctor Speciality</th>
            <th>Provide Feedback</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((appt, index) => (
            <tr key={appt.id}>
              <td>{index + 1}</td>
              <td>{appt.doctorName}</td>
              <td>{appt.speciality}</td>
              <td>
                {(() => {
                  const review = reviews.find(
                    (r) => r.appointmentId === appt.id
                  );
                  return review ? (
                    <span className="feedback-content">{review.feedback}</span>
                  ) : (
                    <button
                      className="feedback-btn"
                      onClick={() => handleFeedbackClick(appt)}
                    >
                      Click Here
                    </button>
                  );
                })()}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedAppointment && (
        <div className="feedback-modal">
          <h3>Provide Feedback for {selectedAppointment.doctorName}</h3>
          <textarea
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            placeholder="Enter your feedback here..."
          ></textarea>
          <div className="button-container">
            <button onClick={handleSubmitReview}>Submit</button>
            <button onClick={() => setSelectedAppointment(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReviewForm;
